function myFunction() {
    var e = document.getElementById("manonavig");
    "navigacija" === e.className ? e.className += " responsive" : e.className = "navigacija";
    var a = document.getElementById("pic");
    "navpic" === a.className ? a.className += " responsive" : a.className = "navpic";
}

function openTab(e, a) {
    var s, n, t;
    for (n = document.getElementsByClassName("ttekstas"), s = 0; s < n.length; s++) n[s].style.display = "none";
    for (t = document.getElementsByClassName("tablinks"), s = 0; s < t.length; s++) t[s].className = t[s].className.replace(" active", "");
    document.getElementById(a).style.display = "block", e.currentTarget.className += " active";
}